<script>
    document.addEventListener("DOMContentLoaded", function () {
        const filterName = document.getElementById('filter-name');
        const filterUsername = document.getElementById('filter-username');
        const filterGroup = document.getElementById('filter-group');
        const filterStatus = document.getElementById('filter-status');
        const filterDate = document.getElementById('filter-date');
    
        const rows = document.querySelectorAll('.alumni-row');
    
        function filterTable() {
            const nameVal = filterName.value.toLowerCase();
            const usernameVal = filterUsername.value.toLowerCase();
            const groupVal = filterGroup.value;
            const statusVal = filterStatus.value.toLowerCase();
            const dateVal = filterDate.value;
    
            rows.forEach(row => {
                const name = row.querySelector('.alumni-name').textContent.toLowerCase();
                const username = row.querySelector('.alumni-username').textContent.toLowerCase();
                const group = row.querySelector('.alumni-group').textContent;
                const status = row.querySelector('.alumni-status').textContent.toLowerCase();
                const date = row.querySelector('.alumni-date').textContent;
    
                const matchName = name.includes(nameVal);
                const matchUsername = username.includes(usernameVal);
                const matchGroup = groupVal === "" || group === groupVal;
                const matchStatus = statusVal === "" || status === statusVal;
                const matchDate = dateVal === "" || date === dateVal;
    
                if (matchName && matchUsername && matchGroup && matchStatus && matchDate) {
                    row.style.display = "";
                } else {
                    row.style.display = "none";
                }
            });
        }
    
        // Attach event listeners
        [filterName, filterUsername, filterGroup, filterStatus, filterDate].forEach(input => {
            input.addEventListener('input', filterTable);
            input.addEventListener('change', filterTable);
        });
    });
    </script>
    
